  public double cgRatio(String dna){  
    int c = dna.indexOf("C");
    int g = dna.indexOf("G");
    double countc = 0.0;
    double countg = 0.0;
    double count = 0.0;
    while (true){  
       if (c == -1){
        break;
       }
       countc++;
       c = dna.indexOf("C", c+1);
           }
    
    while (true){  
       if (g == -1){
        break;
       }
       countg++;
       g = dna.indexOf("G", g+1);
    }
    System.out.println(dna);
    count = countc + countg;
    return ((double) count)/dna.length();
    //return ratio;
  }
  
  public void testRatio(){
    String dna = "ATGCCCGGGAAACCCTGACCCATGGGGTAA";
    System.out.println(cgRatio(dna));
    dna = "ATGCCATAG";
    System.out.println(cgRatio(dna));
    }
  
  public int countCTG(String dna){
    int index = dna.indexOf("CTG");
    int count = 0;
    while (true){  
       if (index == -1){
        break;
       }
       count++;
       index = dna.indexOf("CTG", index+1);
           }
    
    return count;
  }
  
